$(function() {
  var figures, splash, teaser, thumbnail;
  figures = $('figure');
  splash = $('.splash');
  teaser = $('.teaser');
  teaser.on('click', function() {
    return false;
  });
  thumbnail = splash.find('img');
  figures.on('click', function() {
    var img;
    img = $(this).find('img');
    thumbnail.attr('src', img.attr('src'));
    splash.addClass('fullscreen');
    return splash.on('click', function() {
      return $(this).removeClass('fullscreen');
    });
  });
  return figures.on('mousemove', function(e) {
    var img, offset, x, y;
    offset = $(this).offset();
    x = -(e.pageX - offset.left) / 20;
    y = -(e.pageY - offset.top) / 20;
    img = $(this).find('img');
    img.css('left', x + 'px ');
    return img.css('top', y + 'px');
  });
});

